\u043E\u044E \u043D\u0430\u0441\u0442\u043E\u043B\u044C\u043D\u0443\u044E \u0438\u0433\u0440\u0443, \u0432 \u043F\u0435\u0440\u0438\u043E\u0434\u044B, \u043A\u043E\u0433\u0434\u0430 \u043E\u0442\u0435\u0446 \u0436\u0430\u043B\u0435\u043B \u043E \u0442\u043E\u043C, \u0447\u0442\u043E \u043A\u0443\u043F\u0438\u043B \u043C\u043D\u0435 \"\u044D\u0442\u0443 \u0441\u0435\u0433\u0443\" \u0438 \u043F\u0440\u044F\u0442\u0430\u043B \u0435\u0451. \u0412 \u043E\u0431\u0449\u0435\u043C \u044F \u0431\u044B\u043B \"\u0443\u0441\u043F\u0435\u0448\u0435\u043D\". \u041A\u0443\u043F\u0430\u043B\u0441\u044F \u0432 \"\u043F\u0440\u0435\u043C\u0438\u044F\u0445\" \u0438 \u043F\u0440\u043E\u0434\u043E\u043B\u0436\u0430\u043B \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u0442\u044C \u0441\u0432\u043E\u0439 \u043F\u043E\u0442\u0435\u043D\u0446\u0438\u0430\u043B. \u041E\u0434\u043D\u0430\u043A\u043E \u0442\u0435\u043B\u0435\u0432\u0438\u0437\u043E\u0440 \u0438, \u0432 \u043E\u0441\u043E\u0431\u0435\u043D\u043D\u043E\u0441\u0442\u0438, \u043F\u0440\u0438\u0441\u0442\u0430\u0432\u043A\u0438 \u0432\u0441\u0451 \u043B\u0443\u0447\u0448\u0435 \u0443\u0434\u043E\u0432\u043B\u0435\u0442\u0432\u043E\u0440\u044F\u043B\u0438 \u0432\u0438\u0437\u0443\u0430\u043B\u044C\u043D\u044B\u0439 \u0433\u043E\u043B\u043E\u0434, \u043F\u043B\u044E\u0441 \u0440\u0430\u0437\u0432\u0438\u0432\u0430\u043B\u0430\u0441\u044C \u0438\u0445 \u0441\u043F\u043E\u0441\u043E\u0431\u043D\u043E\u0441\u0442\u044C \u0441\u043E\u0437\u0434\u0430\u0432\u0430\u0442\u044C \u0438\u043B\u043B\u044E\u0437\u0438\u044E \u0434\u043E\u043C\u0438\u043D\u0430\u043D\u0442\u043D\u043E\u0441\u0442\u0438 (\u043F\u043E\u0431\u0435\u0434\u044B). \u041F\u043E\u0442\u043E\u043C \u0443 \u043C\u0435\u043D\u044F \u043F\u043E\u044F\u0432\u0438\u043B\u0430\u0441\u044C \"\u0441\u043E\u043D\u0438\u043F\u043B\u0435\u0439\u0441\u0442\u0435\u0448\u043D\" (\u0435\u0449\u0435 \u0431\u043E\u043B\u0435\u0435 \u043A\u0440\u0443\u0442\u0430\u044F \u0433\u0440\u0430\u0444\u0438\u043A\u0430!), \u043F\u043E\u0442\u043E\u043C \"\u043A\u043E\u043C\u043F\u044C\u044E\u0442\u0435\u0440\" (\u0432\u0438\u0434\u0435\u043E\u0438\u0433\u0440\u044B \u0443\u0436\u0435 \u043D\u0435 \u043E\u0433\u0440\u0430\u043D\u0438\u0447\u0438\u0432\u0430\u043B\u0438\u0441\u044C \u043E\u0434\u043D\u043E\u0439 \u0442\u043E\u043B\u044C\u043A\u043E \u0438\u043B\u043B\u044E\u0437\u0438\u0435\u0439 \u0434\u043E\u043C\u0438\u043D\u0430\u043D\u0442\u043D\u043E\u0441\u0442\u0438). \u0412\u043B\u0430\u0441\u0442\u044C \u043E\u0442\u0446\u0430 \u0431\u044B\u043B\u0430 \u043D\u0438\u0447\u0442\u043E\u0436\u043D\u0430 \u043F\u0440\u043E\u0442\u0438\u0432 \u043C\u043E\u0435\u0433\u043E \u043A\u043E\u0432\u0430\u0440\u0441\u0442\u0432\u0430, \u044F \u0431\u044B\u043B \u0433\u043E\u0442\u043E\u0432 \u0433\u043E\u0440\u044B \u0441\u0432\u0435\u0440\u043D\u0443\u0442\u044C, \u043B\u0438\u0448\u044C \u0431\u044B \u043D\u0435 \u043A\u043E\u043F\u0430\u0442\u044C \u043E\u0433\u043E\u0440\u043E\u0434 \u0441 \u0434\u043E\u0444\u0430\u043C\u0438\u043D\u043E\u0432\u044B\u043C\u0438 \u043A\u043B\u0443\u0431\u043D\u044F\u043C\u0438 \u043B\u043E\u0436\u043A\u043E\u0439, \u043A\u043E\u0433\u0434\u0430 \u0435\u0441\u0442\u044C \u0441\u043F\u0440\u044F\u0442\u0430\u043D\u043D\u0430\u044F \u043B\u043E\u043F\u0430\u0442\u0430 (\u043A\u043E\u043D\u0435\u0447\u043D\u043E \u0436\u0435 \u043F\u0440\u0438 \u0443\u0441\u043B\u043E\u0432\u0438\u0438, \u0447\u0442\u043E \"\u0441\u0432\u043E\u0440\u0430\u0447\u0438\u0432\u0430\u043D\u0438\u0435 \u0433\u043E\u0440\" \u043E\u0442\u043D\u0438\u043C\u0435\u0442 \u043C\u0435\u043D\u044C\u0448\u0435 \u044D\u043D\u0435\u0440\u0433\u0438\u0438 \u0447\u0435\u043C \"\u0432\u0441\u043A\u043E\u043F\u043A\u0430 \u043E\u0433\u043E\u0440\u043E\u0434\u0430\"). \u0422\u043E \u0435\u0441\u0442\u044C, \u044F \u0443\u0436\u0435 \u043A\u0440\u0435\u043F\u043A\u043E \u043F\u043E\u0434\u0441\u0435\u043B \u0438 \u0432\u0451\u043B \u0441\u0435\u0431\u044F \u043A\u0430\u043A \u043E\u0431\u044B\u0447\u043D\u044B\u0439 \u0437\u0430\u0432\u0438\u0441\u0438\u043C\u044B\u0439. \u041F\u043E\u043C\u0438\u043C\u043E \u0442\u043E\u0433\u043E, \u044F \u0435\u0449\u0435 \"\u0441\u0438\u0434\u0435\u043B\" \u043D\u0430 \u0441\u043B\u0430\u0434\u043A\u043E\u043C \u0438 \u043C\u0430\u0441\u0442\u0443\u0440\u0431\u0430\u0446\u0438\u0438, \u0430 \u0442\u0432\u043E\u0440\u0447\u0435\u0441\u0442\u0432\u043E\u043C \u043F\u043E\u0447\u0442\u0438 \u043D\u0435 \u0437\u0430\u043D\u0438\u043C\u0430\u043B\u0441\u044F."), mdx("h2", {
    "id": "о-твоих-зависимостях"
  }, "\u041E \u0442\u0432\u043E\u0438\u0445 \u0437\u0430\u0432\u0438\u0441\u0438\u043C\u043E\u0441\u0442\u044F\u0445"), mdx("p", null, "\u0422\u0435\u043F\u0435\u0440\u044C \u0437\u0430\u043C\u0435\u043D\u0438 \u0432\u0438\u0434\u0435\u043E\u0438\u0433\u0440\u044B \u043D\u0430 \u043B\u044E\u0431\u043E\u0439 \"\u043D\u0430\u0440\u043A\u043E\u0442\u0438\u043A\", \u0441\u043F\u043E\u0441\u043E\u0431\u043D\u044B\u0439 \u0434\u0430\u0442\u044C \u0442\u0435\u0431\u0435 \u0434\u043E\u0444\u0430\u043C\u0438\u043D \u043F\u0440\u043E\u0449\u0435 \u0438 \u0431\u044B\u0441\u0442\u0440\u0435\u0435, \u0447\u0435\u043C \u044D\u043D\u0435\u0440\u0433\u043E\u0437\u0430\u0442\u0440\u0430\u0442\u043D\u044B\u043C \u0438 \u0442\u0440\u0435\u0431\u0443\u044E\u0449\u0438\u043C \u043F\u043E\u0442\u0435\u043D\u0446\u0438\u0430\u043B\u0430 \u043F\u0443\u0442\u0451\u043C, \u0438 \u0442\u044B \u043F\u043E\u0439\u043C\u0435\u0448\u044C, \u0447\u0442\u043E \u0432\u0441\u0435 \u043C\u044B \u043F\u043E\u0434\u0441\u0430\u0436\u0435\u043D\u044B \u043D\u0430 \u0441\u0432\u043E\u0438 \"\u0438\u0433\u043B\u044B\".\n\u041A\u0430\u0436\u0434\u0430\u044F \u043D\u0430\u0441 \u043D\u0430\u0441\u044B\u0449\u0430\u0435\u0442 \u0434\u043E\u0444\u0430\u043C\u0438\u043D\u043E\u043C \u0438 \u0432\u043E\u0442 \u043C\u043E\u0437\u0433 \u0443\u0436\u0435 \u0443\u0434\u043E\u0432\u043B\u0435\u0442\u0432\u043E\u0440\u0451\u043D, \u0430 \u0434\u0435\u043D\u044C \u0435\u0449\u0435 \u0442\u043E\u043B\u044C\u043A\u043E \u043D\u0430\u0447\u0430\u043B\u0441\u044F. \u041D\u0438\u0447\u0435\u0433\u043E, \u0441\u043A\u043E\u0440\u043E \u0432\u0435\u0447\u0435\u0440, \u043C\u043E\u0436\u043D\u043E \u043F\u0435\u0440\u0435\u0442\u0435\u0440\u043F\u0435\u0442\u044C, \"\u0434\u043E\u043A\u0438\u043D\u0443\u0442\u044C\u0441\u044F\" \u0438 \u043B\u0435\u0447\u044C \u0441\u043F\u0430\u0442\u044C. \u041D\u0443 \u0438\u043B\u0438 \u0442\u0435\u0440\u043F\u0438\u0448\u044C \u043D\u0435\u043D\u0430\u0432\u0438\u0441\u0442\u043D\u0443\u044E \u0440\u0430\u0431\u043E\u0442\u0443 \u0432\u0435\u0441\u044C \u0434\u0435\u043D\u044C, \u0430 \u043F\u043E\u0442\u043E\u043C \u0432\u0435\u0447\u0435\u0440\u043A\u043E\u043C \"\u0440\u0430\u0441\u0441\u043B\u0430\u0431\u043B\u044F\u0435\u0448\u044C\u0441\u044F \u043A\u0430\u043A \u0446\u0430\u0440\u044C\" \u0432 \u043A\u043E\u043C\u043F\u0430\u043D\u0438\u0438 \u043F\u043E\u0434\u0440\u0443\u0433\u0438-\u0440\u044E\u043C\u043E\u0447\u043A\u0438 \u0438 \u043F\u0440\u0438\u044F\u0442\u0435\u043B\u044F-\u0442\u0435\u043B\u0435\u0432\u0438\u0437\u043E\u0440\u0430. \u041F\u0440\u0438\u0434\u0443\u043C\u0430\u0439 \u0441\u0432\u043E\u0439 \u043F\u043E\u0434\u043E\u0431\u043D\u044B\u0439 \u0441\u0446\u0435\u043D\u0430\u0440\u0438\u0439, \u0443\u043C\u043D\u043E\u0436\u044C \u0435\u0433\u043E \u043D\u0430 \u043C\u043D\u043E\u0433\u043E \u043B\u0435\u0442 \u0438 \u043F\u043E\u043B\u0443\u0447\u0438\u0448\u044C \u043E\u0431\u0440\u0430\u0437 \u0436\u0438\u0437\u043D\u0438 \u0438\u0441\u043A\u0430\u043B\u0435\u0447\u0435\u043D\u043D\u044B\u0445, \u0441 \u0442\u043E\u0447\u043A\u0438 \u0437\u0440\u0435\u043D\u0438\u044F \u043D\u0435\u0439\u0440\u043E\u0431\u0438\u043E\u043B\u043E\u0433\u0438\u0438, \u043B\u044E\u0434\u0435\u0439, \u043D\u0430\u0434\u0435\u0436\u0434\u044B \u043A\u043E\u0442\u043E\u0440\u044B\u0445 \u0443\u0441\u0442\u0440\u0435\u043C\u043B\u0435\u043D\u044B \u0434\u0430\u043B\u0435\u043A\u043E \u0432 \u0431\u0443\u0434\u0443\u0449\u0435\u0435, \u0430 \u043C\u043D\u043E\u0433\u0438\u0435 \u0441\u043E\u0436\u0430\u043B\u0435\u043D\u0438\u044F \u0432 \u043F\u0440\u043E\u0448\u043B\u043E\u0435."), mdx("span", {
